"use strict";(self.webpackChunkmy_website=self.webpackChunkmy_website||[]).push([[1159],{30299:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>i,contentTitle:()=>o,default:()=>c,frontMatter:()=>s,metadata:()=>a,toc:()=>l});const a=JSON.parse('{"id":"A Child\'s Garden of Verses","title":"A Child\'s Garden of Verses","description":"A Child\'s Garden of Verses","source":"@site/learn/A Child\'s Garden of Verses.md","sourceDirName":".","slug":"/A Child\'s Garden of Verses","permalink":"/learn/A Child\'s Garden of Verses","draft":false,"unlisted":false,"editUrl":"https://github.com/hyaliyun/masterpiece/edit/main/website/learn/A Child\'s Garden of Verses.md","tags":[],"version":"current","frontMatter":{},"sidebar":"devDocsSidebar","previous":{"title":"A Charmed Life","permalink":"/learn/A Charmed Life"},"next":{"title":"A Complete Account of the Settlement","permalink":"/learn/A Complete Account of the Settlement"}}');var t=r(74848),h=r(28453);const s={},o=void 0,i={},l=[];function d(e){const n={code:"code",p:"p",pre:"pre",...(0,h.R)(),...e.components};return(0,t.jsxs)(t.Fragment,{children:[(0,t.jsx)(n.p,{children:"A Child's Garden of Verses"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"by Robert Louis Stevenson\n"})}),"\n",(0,t.jsx)(n.p,{children:"To Alison Cunningham\r\nFrom Her Boy"}),"\n",(0,t.jsx)(n.p,{children:"For the long nights you lay awake\r\nAnd watched for my unworthy sake:\r\nFor your most comfortable hand\r\nThat led me through the uneven land:\r\nFor all the story-books you read:\r\nFor all the pains you comforted:"}),"\n",(0,t.jsx)(n.p,{children:"For all you pitied, all you bore,\r\nIn sad and happy days of yore:--\r\nMy second Mother, my first Wife,\r\nThe angel of my infant life--\r\nFrom the sick child, now well and old,\r\nTake, nurse, the little book you hold!"}),"\n",(0,t.jsx)(n.p,{children:"And grant it, Heaven, that all who read\r\nMay find as dear a nurse at need,\r\nAnd every child who lists my rhyme,\r\nIn the bright, fireside, nursery clime,\r\nMay hear it in as kind a voice\r\nAs made my childish days rejoice!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                           R. L. S.\r\n\r\n\r\n                                   Contents\n"})}),"\n",(0,t.jsx)(n.p,{children:"To Alison Cunningham"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"  I   Bed in Summer\r\n II   A Thought\r\nIII   At the Sea-side\r\n IV   Young Night-Thought\r\n  V   Whole Duty of Children\r\n VI   Rain\r\nVII   Pirate Story\n"})}),"\n",(0,t.jsx)(n.p,{children:"VIII   Foreign Lands\r\nIX   Windy Nights\r\nX   Travel\r\nXI   Singing\r\nXII   Looking Forward\r\nXIII   A Good Play\r\nXIV   Where Go the Boats?\r\nXV   Auntie's Skirts\r\nXVI   The Land of Counterpane\r\nXVII   The Land of Nod\r\nXVIII   My Shadow\r\nXIX   System\r\nXX   A Good Boy\r\nXXI   Escape at Bedtime\r\nXXII   Marching Song\r\nXXIII   The Cow\r\nXXIV   The Happy Thought\r\nXXV   The Wind\r\nXXVI   Keepsake Mill\r\nXXVII   Good and Bad Children\r\nXXVIII   Foreign Children\r\nXXIX   The Sun Travels\r\nXXX   The Lamplighter\r\nXXXI   My Bed is a Boat\r\nXXXII   The Moon\r\nXXXIII   The Swing\r\nXXXIV   Time to Rise\r\nXXXV   Looking-glass River\r\nXXXVI   Fairy Bread\r\nXXXVII   From a Railway Carriage\r\nXXXVIII   Winter-time\r\nXXXIX   The Hayloft\r\nXL   Farewell to the Farm\r\nXLI   North-west Passage\r\n1.  Good-Night\r\n2.  Shadow March\r\n3.  In Port"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                The Child Alone\n"})}),"\n",(0,t.jsx)(n.p,{children:"I   The Unseen Playmate\r\nII   My Ship and I\r\nIII   My Kingdom\r\nIV   Picture-books in Winter\r\nV   My Treasures\r\nVI   Block City\r\nVII   The Land of Story-books\r\nVIII   Armies in the Fire\r\nIX   The Little Land"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                  Garden Days\n"})}),"\n",(0,t.jsx)(n.p,{children:"I   Night and Day\r\nII   Nest Eggs\r\nIII   The Flowers\r\nIV   Summer Sun\r\nV   The Dumb Soldier\r\nVI   Autumn Fires\r\nVII   The Gardener\r\nVIII   Historical Associations"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                    Envoys\n"})}),"\n",(0,t.jsx)(n.p,{children:"I   To Willie and Henrietta\r\nII   To My Mother\r\nIII   To Auntie\r\nIV   To Minnie\r\nV   To My Name-Child\r\nVI   To Any Reader"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                          A Child's Garden of Verses\r\n\r\n\r\n\r\n                                       I\r\n                                 Bed in Summer\n"})}),"\n",(0,t.jsx)(n.p,{children:"In winter I get up at night\r\nAnd dress by yellow candle-light.\r\nIn summer quite the other way,\r\nI have to go to bed by day."}),"\n",(0,t.jsx)(n.p,{children:"I have to go to bed and see\r\nThe birds still hopping on the tree,\r\nOr hear the grown-up people's feet\r\nStill going past me in the street."}),"\n",(0,t.jsx)(n.p,{children:"And does it not seem hard to you,\r\nWhen all the sky is clear and blue,\r\nAnd I should like so much to play,\r\nTo have to go to bed by day?"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      II\r\n                                   A Thought\n"})}),"\n",(0,t.jsx)(n.p,{children:"It is very nice to think\r\nThe world is full of meat and drink,\r\nWith little children saying grace\r\nIn every Christian kind of place."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      III\r\n                                At the Sea-side\n"})}),"\n",(0,t.jsx)(n.p,{children:"When I was down beside the sea\r\nA wooden spade they gave to me\r\nTo dig the sandy shore."}),"\n",(0,t.jsx)(n.p,{children:"My holes were empty like a cup.\r\nIn every hole the sea came up,\r\nTill it could come no more."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      IV\r\n                              Young Night-Thought\n"})}),"\n",(0,t.jsx)(n.p,{children:"All night long and every night,\r\nWhen my mama puts out the light,\r\nI see the people marching by,\r\nAs plain as day before my eye."}),"\n",(0,t.jsx)(n.p,{children:"Armies and emperor and kings,\r\nAll carrying different kinds of things,\r\nAnd marching in so grand a way,\r\nYou never saw the like by day."}),"\n",(0,t.jsx)(n.p,{children:"So fine a show was never seen\r\nAt the great circus on the green;\r\nFor every kind of beast and man\r\nIs marching in that caravan."}),"\n",(0,t.jsx)(n.p,{children:"As first they move a little slow,\r\nBut still the faster on they go,\r\nAnd still beside me close I keep\r\nUntil we reach the town of Sleep."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                       V\r\n                            Whole Duty of Children\n"})}),"\n",(0,t.jsx)(n.p,{children:"A child should always say what's true\r\nAnd speak when he is spoken to,\r\nAnd behave mannerly at table;\r\nAt least as far as he is able."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      VI\r\n                                     Rain\n"})}),"\n",(0,t.jsx)(n.p,{children:"The rain is falling all around,\r\nIt falls on field and tree,\r\nIt rains on the umbrellas here,\r\nAnd on the ships at sea."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      VII\r\n                                 Pirate Story\n"})}),"\n",(0,t.jsx)(n.p,{children:"Three of us afloat in the meadow by the swing,\r\nThree of us abroad in the basket on the lea.\r\nWinds are in the air, they are blowing in the spring,\r\nAnd waves are on the meadow like the waves there are at sea."}),"\n",(0,t.jsx)(n.p,{children:"Where shall we adventure, to-day that we're afloat,\r\nWary of the weather and steering by a star?\r\nShall it be to Africa, a-steering of the boat,\r\nTo Providence, or Babylon or off to Malabar?"}),"\n",(0,t.jsx)(n.p,{children:"Hi!  but here's a squadron a-rowing on the sea--\r\nCattle on the meadow a-charging with a roar!\r\nQuick, and we'll escape them, they're as mad as they can be,\r\nThe wicket is the harbour and the garden is the shore."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     VIII\r\n                                 Foreign Lands\n"})}),"\n",(0,t.jsx)(n.p,{children:"Up into the cherry tree\r\nWho should climb but little me?\r\nI held the trunk with both my hands\r\nAnd looked abroad in foreign lands."}),"\n",(0,t.jsx)(n.p,{children:"I saw the next door garden lie,\r\nAdorned with flowers, before my eye,\r\nAnd many pleasant places more\r\nThat I had never seen before."}),"\n",(0,t.jsx)(n.p,{children:"I saw the dimpling river pass\r\nAnd be the sky's blue looking-glass;\r\nThe dusty roads go up and down\r\nWith people tramping in to town."}),"\n",(0,t.jsx)(n.p,{children:"If I could find a higher tree\r\nFarther and farther I should see,\r\nTo where the grown-up river slips\r\nInto the sea among the ships,"}),"\n",(0,t.jsx)(n.p,{children:"To where the road on either hand\r\nLead onward into fairy land,\r\nWhere all the children dine at five,\r\nAnd all the playthings come alive."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      IX\r\n                                 Windy Nights\n"})}),"\n",(0,t.jsx)(n.p,{children:"Whenever the moon and stars are set,\r\nWhenever the wind is high,\r\nAll night long in the dark and wet,\r\nA man goes riding by.\r\nLate in the night when the fires are out,\r\nWhy does he gallop and gallop about?"}),"\n",(0,t.jsx)(n.p,{children:"Whenever the trees are crying aloud,\r\nAnd ships are tossed at sea,\r\nBy, on the highway, low and loud,\r\nBy at the gallop goes he.\r\nBy at the gallop he goes, and then\r\nBy he comes back at the gallop again."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                       X\r\n                                    Travel\n"})}),"\n",(0,t.jsx)(n.p,{children:"I should like to rise and go\r\nWhere the golden apples grow;--\r\nWhere below another sky\r\nParrot islands anchored lie,\r\nAnd, watched by cockatoos and goats,\r\nLonely Crusoes building boats;--\r\nWhere in sunshine reaching out\r\nEastern cities, miles about,\r\nAre with mosque and minaret\r\nAmong sandy gardens set,\r\nAnd the rich goods from near and far\r\nHang for sale in the bazaar;--\r\nWhere the Great Wall round China goes,\r\nAnd on one side the desert blows,\r\nAnd with the voice and bell and drum,\r\nCities on the other hum;--\r\nWhere are forests hot as fire,\r\nWide as England, tall as a spire,\r\nFull of apes and cocoa-nuts\r\nAnd the negro hunters' huts;--\r\nWhere the knotty crocodile\r\nLies and blinks in the Nile,\r\nAnd the red flamingo flies\r\nHunting fish before his eyes;--\r\nWhere in jungles near and far,\r\nMan-devouring tigers are,\r\nLying close and giving ear\r\nLest the hunt be drawing near,\r\nOr a comer-by be seen\r\nSwinging in the palanquin;--\r\nWhere among the desert sands\r\nSome deserted city stands,\r\nAll its children, sweep and prince,\r\nGrown to manhood ages since,\r\nNot a foot in street or house,\r\nNot a stir of child or mouse,\r\nAnd when kindly falls the night,\r\nIn all the town no spark of light.\r\nThere I'll come when I'm a man\r\nWith a camel caravan;\r\nLight a fire in the gloom\r\nOf some dusty dining-room;\r\nSee the pictures on the walls,\r\nHeroes fights and festivals;\r\nAnd in a corner find the toys\r\nOf the old Egyptian boys."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      XI\r\n                                    Singing\n"})}),"\n",(0,t.jsx)(n.p,{children:"Of speckled eggs the birdie sings\r\nAnd nests among the trees;\r\nThe sailor sings of ropes and things\r\nIn ships upon the seas."}),"\n",(0,t.jsx)(n.p,{children:"The children sing in far Japan,\r\nThe children sing in Spain;\r\nThe organ with the organ man\r\nIs singing in the rain."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      XII\r\n                                Looking Forward\n"})}),"\n",(0,t.jsx)(n.p,{children:"When I am grown to man's estate\r\nI shall be very proud and great,\r\nAnd tell the other girls and boys\r\nNot to meddle with my toys."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XIII\r\n                                  A Good Play\n"})}),"\n",(0,t.jsx)(n.p,{children:"We built a ship upon the stairs\r\nAll made of the back-bedroom chairs,\r\nAnd filled it full of soft pillows\r\nTo go a-sailing on the billows."}),"\n",(0,t.jsx)(n.p,{children:'We took a saw and several nails,\r\nAnd water in the nursery pails;\r\nAnd Tom said, "Let us also take\r\nAn apple and a slice of cake;"--\r\nWhich was enough for Tom and me\r\nTo go a-sailing on, till tea.'}),"\n",(0,t.jsx)(n.p,{children:"We sailed along for days and days,\r\nAnd had the very best of plays;\r\nBut Tom fell out and hurt his knee,\r\nSo there was no one left but me."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      XIV\r\n                              Where Go the Boats?\n"})}),"\n",(0,t.jsx)(n.p,{children:"Dark brown is the river,\r\nGolden is the sand.\r\nIt flows along for ever,\r\nWith trees on either hand."}),"\n",(0,t.jsx)(n.p,{children:"Green leaves a-floating,\r\nCastles of the foam,\r\nBoats of mine a-boating--\r\nWhere will all come home?"}),"\n",(0,t.jsx)(n.p,{children:"On goes the river\r\nAnd out past the mill,\r\nAway down the valley,\r\nAway down the hill."}),"\n",(0,t.jsx)(n.p,{children:"Away down the river,\r\nA hundred miles or more,\r\nOther little children\r\nShall bring my boats ashore."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      XV\r\n                                Auntie's Skirts\n"})}),"\n",(0,t.jsx)(n.p,{children:"Whenever Auntie moves around,\r\nHer dresses make a curious sound,\r\nThey trail behind her up the floor,\r\nAnd trundle after through the door."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      XVI\r\n                            The Land of Counterpane\n"})}),"\n",(0,t.jsx)(n.p,{children:"When I was sick and lay a-bed,\r\nI had two pillows at my head,\r\nAnd all my toys beside me lay,\r\nTo keep me happy all the day."}),"\n",(0,t.jsx)(n.p,{children:"And sometimes for an hour or so\r\nI watched my leaden soldiers go,\r\nWith different uniforms and drills,\r\nAmong the bed-clothes, through the hills;"}),"\n",(0,t.jsx)(n.p,{children:"And sometimes sent my ships in fleets\r\nAll up and down among the sheets;\r\nOr brought my trees and houses out,\r\nAnd planted cities all about."}),"\n",(0,t.jsx)(n.p,{children:"I was the giant great and still\r\nThat sits upon the pillow-hill,\r\nAnd sees before him, dale and plain,\r\nThe pleasant land of counterpane."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XVII\r\n                                The Land of Nod\n"})}),"\n",(0,t.jsx)(n.p,{children:"From breakfast on through all the day\r\nAt home among my friends I stay,\r\nBut every night I go abroad\r\nAfar into the land of Nod."}),"\n",(0,t.jsx)(n.p,{children:"All by myself I have to go,\r\nWith none to tell me what to do--\r\nAll alone beside the streams\r\nAnd up the mountain-sides of dreams."}),"\n",(0,t.jsx)(n.p,{children:"The strangest things are these for me,\r\nBoth things to eat and things to see,\r\nAnd many frightening sights abroad\r\nTill morning in the land of Nod."}),"\n",(0,t.jsx)(n.p,{children:"Try as I like to find the way,\r\nI never can get back by day,\r\nNor can remember plain and clear\r\nThe curious music that I hear."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XVIII\r\n                                   My Shadow\n"})}),"\n",(0,t.jsx)(n.p,{children:"I have a little shadow that goes in and out with me,\r\nAnd what can be the use of him is more than I can see.\r\nHe is very, very like me from the heels up to the head;\r\nAnd I see him jump before me, when I jump into my bed."}),"\n",(0,t.jsx)(n.p,{children:"The funniest thing about him is the way he likes to grow--\r\nNot at all like proper children, which is always very slow;\r\nFor he sometimes shoots up taller like an india-rubber ball,\r\nAnd he sometimes goes so little that there's none of him at all."}),"\n",(0,t.jsx)(n.p,{children:"He hasn't got a notion of how children ought to play,\r\nAnd can only make a fool of me in every sort of way.\r\nHe stays so close behind me, he's a coward you can see;\r\nI'd think shame to stick to nursie as that shadow sticks to me!"}),"\n",(0,t.jsx)(n.p,{children:"One morning, very early, before the sun was up,\r\nI rose and found the shining dew on every buttercup;\r\nBut my lazy little shadow, like an arrant sleepy-head,\r\nHad stayed at home behind me and was fast asleep in bed."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      XIX\r\n                                    System\n"})}),"\n",(0,t.jsx)(n.p,{children:"Every night my prayers I say,\r\nAnd get my dinner every day;\r\nAnd every day that I've been good,\r\nI get an orange after food."}),"\n",(0,t.jsx)(n.p,{children:"The child that is not clean and neat,\r\nWith lots of toys and things to eat,\r\nHe is a naughty child, I'm sure--\r\nOr else his dear papa is poor."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      XX\r\n                                  A Good Boy\n"})}),"\n",(0,t.jsx)(n.p,{children:"I woke before the morning, I was happy all the day,\r\nI never said an ugly word, but smiled and stuck to play."}),"\n",(0,t.jsx)(n.p,{children:"And now at last the sun is going down behind the wood,\r\nAnd I am very happy, for I know that I've been good."}),"\n",(0,t.jsx)(n.p,{children:"My bed is waiting cool and fresh, with linen smooth and fair,\r\nAnd I must be off to sleepsin-by, and not forget my prayer."}),"\n",(0,t.jsx)(n.p,{children:"I know that, till to-morrow I shall see the sun arise,\r\nNo ugly dream shall fright my mind, no ugly sight my eyes."}),"\n",(0,t.jsx)(n.p,{children:"But slumber hold me tightly till I waken in the dawn,\r\nAnd hear the thrushes singing in the lilacs round the lawn."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      XXI\r\n                               Escape at Bedtime\n"})}),"\n",(0,t.jsx)(n.p,{children:"The lights from the parlour and kitchen shone out\r\nThrough the blinds and the windows and bars;\r\nAnd high overhead and all moving about,\r\nThere were thousands of millions of stars.\r\nThere ne'er were such thousands of leaves on a tree,\r\nNor of people in church or the Park,\r\nAs the crowds of the stars that looked down upon me,\r\nAnd that glittered and winked in the dark."}),"\n",(0,t.jsx)(n.p,{children:"The Dog, and the Plough, and the Hunter, and all,\r\nAnd the star of the sailor, and Mars,\r\nThese shown in the sky, and the pail by the wall\r\nWould be half full of water and stars.\r\nThey saw me at last, and they chased me with cries,\r\nAnd they soon had me packed into bed;\r\nBut the glory kept shining and bright in my eyes,\r\nAnd the stars going round in my head."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XXII\r\n                                 Marching Song\n"})}),"\n",(0,t.jsx)(n.p,{children:"Bring the comb and play upon it!\r\nMarching, here we come!\r\nWillie cocks his highland bonnet,\r\nJohnnie beats the drum."}),"\n",(0,t.jsx)(n.p,{children:"Mary Jane commands the party,\r\nPeter leads the rear;\r\nFeet in time, alert and hearty,\r\nEach a Grenadier!"}),"\n",(0,t.jsx)(n.p,{children:"All in the most martial manner\r\nMarching double-quick;\r\nWhile the napkin, like a banner,\r\nWaves upon the stick!"}),"\n",(0,t.jsx)(n.p,{children:"Here's enough of fame and pillage,\r\nGreat commander Jane!\r\nNow that we've been round the village,\r\nLet's go home again."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XXIII\r\n                                    The Cow\n"})}),"\n",(0,t.jsx)(n.p,{children:"The friendly cow all red and white,\r\nI love with all my heart:\r\nShe gives me cream with all her might,\r\nTo eat with apple-tart."}),"\n",(0,t.jsx)(n.p,{children:"She wanders lowing here and there,\r\nAnd yet she cannot stray,\r\nAll in the pleasant open air,\r\nThe pleasant light of day;"}),"\n",(0,t.jsx)(n.p,{children:"And blown by all the winds that pass\r\nAnd wet with all the showers,\r\nShe walks among the meadow grass\r\nAnd eats the meadow flowers."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XXIV\r\n                                 Happy Thought\n"})}),"\n",(0,t.jsx)(n.p,{children:"The world is so full of a number of things,\r\nI'm sure we should all be as happy as kings."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      XXV\r\n                                   The Wind\n"})}),"\n",(0,t.jsx)(n.p,{children:"I saw you toss the kites on high\r\nAnd blow the birds about the sky;\r\nAnd all around I heard you pass,\r\nLike ladies' skirts across the grass--\r\nO wind, a-blowing all day long,\r\nO wind, that sings so loud a song!"}),"\n",(0,t.jsx)(n.p,{children:"I saw the different things you did,\r\nBut always you yourself you hid.\r\nI felt you push, I heard you call,\r\nI could not see yourself at all--\r\nO wind, a-blowing all day long,\r\nO wind, that sings so loud a song!"}),"\n",(0,t.jsx)(n.p,{children:"O you that are so strong and cold,\r\nO blower, are you young or old?\r\nAre you a beast of field and tree,\r\nOr just a stronger child than me?\r\nO wind, a-blowing all day long,\r\nO wind, that sings so loud a song!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XXVI\r\n                                 Keepsake Mill\n"})}),"\n",(0,t.jsx)(n.p,{children:"Over the borders, a sin without pardon,\r\nBreaking the branches and crawling below,\r\nOut through the breach in the wall of the garden,\r\nDown by the banks of the river we go."}),"\n",(0,t.jsx)(n.p,{children:"Here is a mill with the humming of thunder,\r\nHere is the weir with the wonder of foam,\r\nHere is the sluice with the race running under--\r\nMarvellous places, though handy to home!"}),"\n",(0,t.jsx)(n.p,{children:"Sounds of the village grow stiller and stiller,\r\nStiller the note of the birds on the hill;\r\nDusty and dim are the eyes of the miller,\r\nDeaf are his ears with the moil of the mill."}),"\n",(0,t.jsx)(n.p,{children:"Years may go by, and the wheel in the river\r\nWheel as it wheels for us, children, to-day,\r\nWheel and keep roaring and foaming for ever\r\nLong after all of the boys are away."}),"\n",(0,t.jsx)(n.p,{children:"Home for the Indies and home from the ocean,\r\nHeroes and soldiers we all will come home;\r\nStill we shall find the old mill wheel in motion,\r\nTurning and churning that river to foam."}),"\n",(0,t.jsx)(n.p,{children:"You with the bean that I gave when we quarrelled,\r\nI with your marble of Saturday last,\r\nHonoured and old and all gaily apparelled,\r\nHere we shall meet and remember the past."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XXVII\r\n                             Good and Bad Children\n"})}),"\n",(0,t.jsx)(n.p,{children:"Children, you are very little,\r\nAnd your bones are very brittle;\r\nIf you would grow great and stately,\r\nYou must try to walk sedately."}),"\n",(0,t.jsx)(n.p,{children:"You must still be bright and quiet,\r\nAnd content with simple diet;\r\nAnd remain, through all bewild'ring,\r\nInnocent and honest children."}),"\n",(0,t.jsx)(n.p,{children:"Happy hearts and happy faces,\r\nHappy play in grassy places--\r\nThat was how in ancient ages,\r\nChildren grew to kings and sages."}),"\n",(0,t.jsx)(n.p,{children:"But the unkind and the unruly,\r\nAnd the sort who eat unduly,\r\nThey must never hope for glory--\r\nTheirs is quite a different story!"}),"\n",(0,t.jsx)(n.p,{children:"Cruel children, crying babies,\r\nAll grow up as geese and gabies,\r\nHated, as their age increases,\r\nBy their nephews and their nieces."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                    XXVIII\r\n                               Foreign Children\n"})}),"\n",(0,t.jsx)(n.p,{children:"Little Indian, Sioux, or Crow,\r\nLittle frosty Eskimo,\r\nLittle Turk or Japanee,\r\nOh! don't you wish that you were me?"}),"\n",(0,t.jsx)(n.p,{children:"You have seen the scarlet trees\r\nAnd the lions over seas;\r\nYou have eaten ostrich eggs,\r\nAnd turned the turtle off their legs."}),"\n",(0,t.jsx)(n.p,{children:"Such a life is very fine,\r\nBut it's not so nice as mine:\r\nYou must often as you trod,\r\nHave wearied NOT to be abroad."}),"\n",(0,t.jsx)(n.p,{children:"You have curious things to eat,\r\nI am fed on proper meat;\r\nYou must dwell upon the foam,\r\nBut I am safe and live at home.\r\nLittle Indian, Sioux or Crow,\r\nLittle frosty Eskimo,\r\nLittle Turk or Japanee,\r\nOh! don't you wish that you were me?"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XXIX\r\n                                The Sun Travels\n"})}),"\n",(0,t.jsx)(n.p,{children:"The sun is not a-bed, when I\r\nAt night upon my pillow lie;\r\nStill round the earth his way he takes,\r\nAnd morning after morning makes."}),"\n",(0,t.jsx)(n.p,{children:"While here at home, in shining day,\r\nWe round the sunny garden play,\r\nEach little Indian sleepy-head\r\nIs being kissed and put to bed."}),"\n",(0,t.jsx)(n.p,{children:"And when at eve I rise form tea,\r\nDay dawns beyond the Atlantic Sea;\r\nAnd all the children in the west\r\nAre getting up and being dressed."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      XXX\r\n                                The Lamplighter\n"})}),"\n",(0,t.jsx)(n.p,{children:"My tea is nearly ready and the sun has left the sky.\r\nIt's time to take the window to see Leerie going by;\r\nFor every night at teatime and before you take your seat,\r\nWith lantern and with ladder he comes posting up the street."}),"\n",(0,t.jsx)(n.p,{children:"Now Tom would be a driver and Maria go to sea,\r\nAnd my papa's a banker and as rich as he can be;\r\nBut I, when I am stronger and can choose what I'm to do,\r\nO Leerie, I'll go round at night and light the lamps with you!"}),"\n",(0,t.jsx)(n.p,{children:"For we are very lucky, with a lamp before the door,\r\nAnd Leerie stops to light it as he lights so many more;\r\nAnd oh! before you hurry by with ladder and with light;\r\nO Leerie, see a little child and nod to him to-night!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XXXI\r\n                               My Bed is a Boat\n"})}),"\n",(0,t.jsx)(n.p,{children:"My bed is like a little boat;\r\nNurse helps me in when I embark;\r\nShe girds me in my sailor's coat\r\nAnd starts me in the dark."}),"\n",(0,t.jsx)(n.p,{children:"At night I go on board and say\r\nGood-night to all my friends on shore;\r\nI shut my eyes and sail away\r\nAnd see and hear no more."}),"\n",(0,t.jsx)(n.p,{children:"And sometimes things to bed I take,\r\nAs prudent sailors have to do;\r\nPerhaps a slice of wedding-cake,\r\nPerhaps a toy or two."}),"\n",(0,t.jsx)(n.p,{children:"All night across the dark we steer;\r\nBut when the day returns at last,\r\nSafe in my room beside the pier,\r\nI find my vessel fast."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XXXII\r\n                                   The Moon\n"})}),"\n",(0,t.jsx)(n.p,{children:"The moon has a face like the clock in the hall;\r\nShe shines on thieves on the garden wall,\r\nOn streets and fields and harbour quays,\r\nAnd birdies asleep in the forks of the trees."}),"\n",(0,t.jsx)(n.p,{children:"The squalling cat and the squeaking mouse,\r\nThe howling dog by the door of the house,\r\nThe bat that lies in bed at noon,\r\nAll love to be out by the light of the moon."}),"\n",(0,t.jsx)(n.p,{children:"But all of the things that belong to the day\r\nCuddle to sleep to be out of her way;\r\nAnd flowers and children close their eyes\r\nTill up in the morning the sun shall arise."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                    XXXIII\r\n                                   The Swing\n"})}),"\n",(0,t.jsx)(n.p,{children:"How do you like to go up in a swing,\r\nUp in the air so blue?\r\nOh, I do think it the pleasantest thing\r\nEver a child can do!"}),"\n",(0,t.jsx)(n.p,{children:"Up in the air and over the wall,\r\nTill I can see so wide,\r\nRiver and trees and cattle and all\r\nOver the countryside--"}),"\n",(0,t.jsx)(n.p,{children:"Till I look down on the garden green,\r\nDown on the roof so brown--\r\nUp in the air I go flying again,\r\nUp in the air and down!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XXXIV\r\n                                 Time to Rise\n"})}),"\n",(0,t.jsx)(n.p,{children:"A birdie with a yellow bill\r\nHopped upon my window sill,\r\nCocked his shining eye and said:\r\n\"Ain't you 'shamed, you sleepy-head!\""}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XXXV\r\n                              Looking-glass River\n"})}),"\n",(0,t.jsx)(n.p,{children:"Smooth it glides upon its travel,\r\nHere a wimple, there a gleam--\r\nO the clean gravel!\r\nO the smooth stream!"}),"\n",(0,t.jsx)(n.p,{children:"Sailing blossoms, silver fishes,\r\nPave pools as clear as air--\r\nHow a child wishes\r\nTo live down there!"}),"\n",(0,t.jsx)(n.p,{children:"We can see our colored faces\r\nFloating on the shaken pool\r\nDown in cool places,\r\nDim and very cool;"}),"\n",(0,t.jsx)(n.p,{children:"Till a wind or water wrinkle,\r\nDipping marten, plumping trout,\r\nSpreads in a twinkle\r\nAnd blots all out."}),"\n",(0,t.jsx)(n.p,{children:"See the rings pursue each other;\r\nAll below grows black as night,\r\nJust as if mother\r\nHad blown out the light!"}),"\n",(0,t.jsx)(n.p,{children:"Patience, children, just a minute--\r\nSee the spreading circles die;\r\nThe stream and all in it\r\nWill clear by-and-by."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XXXVI\r\n                                  Fairy Bread\n"})}),"\n",(0,t.jsx)(n.p,{children:"Come up here, O dusty feet!\r\nHere is fairy bread to eat.\r\nHere in my retiring room,\r\nChildren, you may dine\r\nOn the golden smell of broom\r\nAnd the shade of pine;\r\nAnd when you have eaten well,\r\nFairy stories hear and tell."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                    XXXVII\r\n                            From a Railway Carriage\n"})}),"\n",(0,t.jsx)(n.p,{children:"Faster than fairies, faster than witches,\r\nBridges and houses, hedges and ditches;\r\nAnd charging along like troops in a battle\r\nAll through the meadows the horses and cattle:\r\nAll of the sights of the hill and the plain\r\nFly as thick as driving rain;\r\nAnd ever again, in the wink of an eye,\r\nPainted stations whistle by.\r\nHere is a child who clambers and scrambles,\r\nAll by himself and gathering brambles;\r\nHere is a tramp who stands and gazes;\r\nAnd here is the green for stringing the daisies!\r\nHere is a cart runaway in the road\r\nLumping along with man and load;\r\nAnd here is a mill, and there is a river:\r\nEach a glimpse and gone forever!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                    XXXVIII\r\n                                  Winter-time\n"})}),"\n",(0,t.jsx)(n.p,{children:"Late lies the wintry sun a-bed,\r\nA frosty, fiery sleepy-head;\r\nBlinks but an hour or two; and then,\r\nA blood-red orange, sets again."}),"\n",(0,t.jsx)(n.p,{children:"Before the stars have left the skies,\r\nAt morning in the dark I rise;\r\nAnd shivering in my nakedness,\r\nBy the cold candle, bathe and dress."}),"\n",(0,t.jsx)(n.p,{children:"Close by the jolly fire I sit\r\nTo warm my frozen bones a bit;\r\nOr with a reindeer-sled, explore\r\nThe colder countries round the door."}),"\n",(0,t.jsx)(n.p,{children:"When to go out, my nurse doth wrap\r\nMe in my comforter and cap;\r\nThe cold wind burns my face, and blows\r\nIts frosty pepper up my nose."}),"\n",(0,t.jsx)(n.p,{children:"Black are my steps on silver sod;\r\nThick blows my frosty breath abroad;\r\nAnd tree and house, and hill and lake,\r\nAre frosted like a wedding cake."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     XXXIX\r\n                                  The Hayloft\n"})}),"\n",(0,t.jsx)(n.p,{children:"Through all the pleasant meadow-side\r\nThe grass grew shoulder-high,\r\nTill the shining scythes went far and wide\r\nAnd cut it down to dry."}),"\n",(0,t.jsx)(n.p,{children:"Those green and sweetly smelling crops\r\nThey led the waggons home;\r\nAnd they piled them here in mountain tops\r\nFor mountaineers to roam."}),"\n",(0,t.jsx)(n.p,{children:"Here is Mount Clear, Mount Rusty-Nail,\r\nMount Eagle and Mount High;--\r\nThe mice that in these mountains dwell,\r\nNo happier are than I!"}),"\n",(0,t.jsx)(n.p,{children:"Oh, what a joy to clamber there,\r\nOh, what a place for play,\r\nWith the sweet, the dim, the dusty air,\r\nThe happy hills of hay!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      XL\r\n                             Farewell to the Farm\n"})}),"\n",(0,t.jsx)(n.p,{children:"The coach is at the door at last;\r\nThe eager children, mounting fast\r\nAnd kissing hands, in chorus sing:\r\nGood-bye, good-bye, to everything!"}),"\n",(0,t.jsx)(n.p,{children:"To house and garden, field and lawn,\r\nThe meadow-gates we swang upon,\r\nTo pump and stable, tree and swing,\r\nGood-bye, good-bye, to everything!"}),"\n",(0,t.jsx)(n.p,{children:"And fare you well for evermore,\r\nO ladder at the hayloft door,\r\nO hayloft where the cobwebs cling,\r\nGood-bye, good-bye, to everything!"}),"\n",(0,t.jsx)(n.p,{children:"Crack goes the whip, and off we go;\r\nThe trees and houses smaller grow;\r\nLast, round the woody turn we sing:\r\nGood-bye, good-bye, to everything!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      XLI\r\n                              North-west Passage\r\n\r\n                                 1. Good-night\n"})}),"\n",(0,t.jsx)(n.p,{children:"Then the bright lamp is carried in,\r\nThe sunless hours again begin;\r\nO'er all without, in field and lane,\r\nThe haunted night returns again."}),"\n",(0,t.jsx)(n.p,{children:"Now we behold the embers flee\r\nAbout the firelit hearth; and see\r\nOur faces painted as we pass,\r\nLike pictures, on the window glass."}),"\n",(0,t.jsx)(n.p,{children:"Must we to bed indeed?  Well then,\r\nLet us arise and go like men,\r\nAnd face with an undaunted tread\r\nThe long black passage up to bed."}),"\n",(0,t.jsx)(n.p,{children:"Farewell, O brother, sister, sire!\r\nO pleasant party round the fire!\r\nThe songs you sing, the tales you tell,\r\nTill far to-morrow, fare you well!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                2. Shadow March\n"})}),"\n",(0,t.jsx)(n.p,{children:"All around the house is the jet-black night;\r\nIt stares through the window-pane;\r\nIt crawls in the corners, hiding from the light,\r\nAnd it moves with the moving flame."}),"\n",(0,t.jsx)(n.p,{children:"Now my little heart goes a beating like a drum,\r\nWith the breath of the Bogies in my hair;\r\nAnd all around the candle and the crooked shadows come,\r\nAnd go marching along up the stair."}),"\n",(0,t.jsx)(n.p,{children:"The shadow of the balusters, the shadow of the lamp,\r\nThe shadow of the child that goes to bed--\r\nAll the wicked shadows coming tramp, tramp, tramp,\r\nWith the black night overhead."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                  3. In Port\n"})}),"\n",(0,t.jsx)(n.p,{children:"Last, to the chamber where I lie\r\nMy fearful footsteps patter nigh,\r\nAnd come out from the cold and gloom\r\nInto my warm and cheerful room."}),"\n",(0,t.jsx)(n.p,{children:"There, safe arrived, we turn about\r\nTo keep the coming shadows out,\r\nAnd close the happy door at last\r\nOn all the perils that we past."}),"\n",(0,t.jsx)(n.p,{children:"Then, when mamma goes by to bed,\r\nShe shall come in with tip-toe tread,\r\nAnd see me lying warm and fast\r\nAnd in the land of Nod at last."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                THE CHILD ALONE\r\n\r\n\r\n                                       I\r\n                              The Unseen Playmate\n"})}),"\n",(0,t.jsx)(n.p,{children:"When children are playing alone on the green,\r\nIn comes the playmate that never was seen.\r\nWhen children are happy and lonely and good,\r\nThe Friend of the Children comes out of the wood."}),"\n",(0,t.jsx)(n.p,{children:"Nobody heard him, and nobody saw,\r\nHis is a picture you never could draw,\r\nBut he's sure to be present, abroad or at home,\r\nWhen children are happy and playing alone."}),"\n",(0,t.jsx)(n.p,{children:"He lies in the laurels, he runs on the grass,\r\nHe sings when you tinkle the musical glass;\r\nWhene'er you are happy and cannot tell why,\r\nThe Friend of the Children is sure to be by!"}),"\n",(0,t.jsx)(n.p,{children:"He loves to be little, he hates to be big,\r\n'T is he that inhabits the caves that you dig;\r\n'T is he when you play with your soldiers of tin\r\nThat sides with the Frenchmen and never can win."}),"\n",(0,t.jsx)(n.p,{children:"'T is he, when at night you go off to your bed,\r\nBids you go to sleep and not trouble your head;\r\nFor wherever they're lying, in cupboard or shelf,\r\n'T is he will take care of your playthings himself!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      II\r\n                                 My Ship and I\n"})}),"\n",(0,t.jsx)(n.p,{children:"O it's I that am the captain of a tidy little ship,\r\nOf a ship that goes a sailing on the pond;\r\nAnd my ship it keeps a-turning  all around and all about;\r\nBut when I'm a little older, I shall find the secret out\r\nHow to send my vessel sailing on beyond."}),"\n",(0,t.jsx)(n.p,{children:"For I mean to grow a little as the dolly at the helm,\r\nAnd the dolly I intend to come alive;\r\nAnd with him beside to help me, it's a-sailing I shall go,\r\nIt's a-sailing on the water, when the jolly breezes blow\r\nAnd the vessel goes a dive-dive-dive."}),"\n",(0,t.jsx)(n.p,{children:"O it's then you'll see me sailing through the rushes and the reeds,\r\nAnd you'll hear the water singing at the prow;\r\nFor beside the dolly sailor, I'm to voyage and explore,\r\nTo land upon the island where no dolly was before,\r\nAnd to fire the penny cannon in the bow."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      III\r\n                                  My Kingdom\n"})}),"\n",(0,t.jsx)(n.p,{children:"Down by a shining water well\r\nI found a very little dell,\r\nNo higher than my head.\r\nThe heather and the gorse about\r\nIn summer bloom were coming out,\r\nSome yellow and some red."}),"\n",(0,t.jsx)(n.p,{children:"I called the little pool a sea;\r\nThe little hills were big to me;\r\nFor I am very small.\r\nI made a boat, I made a town,\r\nI searched the caverns up and down,\r\nAnd named them one and all."}),"\n",(0,t.jsx)(n.p,{children:"And all about was mine, I said,\r\nThe little sparrows overhead,\r\nThe little minnows too.\r\nThis was the world and I was king;\r\nFor me the bees came by to sing,\r\nFor me the swallows flew."}),"\n",(0,t.jsx)(n.p,{children:"I played there were no deeper seas,\r\nNor any wider plains than these,\r\nNor other kings than me.\r\nAt last I heard my mother call\r\nOut from the house at evenfall,\r\nTo call me home to tea."}),"\n",(0,t.jsx)(n.p,{children:"And I must rise and leave my dell,\r\nAnd leave my dimpled water well,\r\nAnd leave my heather blooms.\r\nAlas! and as my home I neared,\r\nHow very big my nurse appeared.\r\nHow great and cool the rooms!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      IV\r\n                            Picture-books in Winter\n"})}),"\n",(0,t.jsx)(n.p,{children:"Summer fading, winter comes--\r\nFrosty mornings, tingling thumbs,\r\nWindow robins, winter rooks,\r\nAnd the picture story-books."}),"\n",(0,t.jsx)(n.p,{children:"Water now is turned to stone\r\nNurse and I can walk upon;\r\nStill we find the flowing brooks\r\nIn the picture story-books."}),"\n",(0,t.jsx)(n.p,{children:"All the pretty things put by,\r\nWait upon the children's eye,\r\nSheep and shepherds, trees and crooks,\r\nIn the picture story-books."}),"\n",(0,t.jsx)(n.p,{children:"We may see how all things are\r\nSeas and cities, near and far,\r\nAnd the flying fairies' looks,\r\nIn the picture story-books."}),"\n",(0,t.jsx)(n.p,{children:"How am I to sing your praise,\r\nHappy chimney-corner days,\r\nSitting safe in nursery nooks,\r\nReading picture story-books?"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                       V\r\n                                 My Treasures\n"})}),"\n",(0,t.jsx)(n.p,{children:"These nuts, that I keep in the back of the nest,\r\nWhere all my tin soldiers are lying at rest,\r\nWere gathered in Autumn by nursie and me\r\nIn a wood with a well by the side of the sea."}),"\n",(0,t.jsx)(n.p,{children:"This whistle we made (and how clearly it sounds!)\r\nBy the side of a field at the end of the grounds.\r\nOf a branch of a plane, with a knife of my own,\r\nIt was nursie who made it, and nursie alone!"}),"\n",(0,t.jsx)(n.p,{children:"The stone, with the white and the yellow and grey,\r\nWe discovered I cannot tell HOW far away;\r\nAnd I carried it back although weary and cold,\r\nFor though father denies it, I'm sure it is gold."}),"\n",(0,t.jsx)(n.p,{children:"But of all my treasures the last is the king,\r\nFor there's very few children possess such a thing;\r\nAnd that is a chisel, both handle and blade,\r\nWhich a man who was really a carpenter made."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      VI\r\n                                  Block City\n"})}),"\n",(0,t.jsx)(n.p,{children:"What are you able to build with your blocks?\r\nCastles and palaces, temples and docks.\r\nRain may keep raining, and others go roam,\r\nBut I can be happy and building at home."}),"\n",(0,t.jsx)(n.p,{children:"Let the sofa be mountains, the carpet be sea,\r\nThere I'll establish a city for me:\r\nA kirk and a mill and a palace beside,\r\nAnd a harbour as well where my vessels may ride."}),"\n",(0,t.jsx)(n.p,{children:"Great is the palace with pillar and wall,\r\nA sort of a tower on the top of it all,\r\nAnd steps coming down in an orderly way\r\nTo where my toy vessels lie safe in the bay."}),"\n",(0,t.jsx)(n.p,{children:"This one is sailing and that one is moored:\r\nHark to the song of the sailors aboard!\r\nAnd see, on the steps of my palace, the kings\r\nComing and going with presents and things!"}),"\n",(0,t.jsx)(n.p,{children:"Yet as I saw it, I see it again,\r\nThe kirk and the palace, the ships and the men,\r\nAnd as long as I live and where'er I may be,\r\nI'll always remember my town by the sea."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      VII\r\n                            The Land of Story-books\n"})}),"\n",(0,t.jsx)(n.p,{children:"At evening when the lamp is lit,\r\nAround the fire my parents sit;\r\nThey sit at home and talk and sing,\r\nAnd do not play at anything."}),"\n",(0,t.jsx)(n.p,{children:"Now, with my little gun, I crawl\r\nAll in the dark along the wall,\r\nAnd follow round the forest track\r\nAway behind the sofa back."}),"\n",(0,t.jsx)(n.p,{children:"There, in the night, where none can spy,\r\nAll in my hunter's camp I lie,\r\nAnd play at books that I have read\r\nTill it is time to go to bed."}),"\n",(0,t.jsx)(n.p,{children:"These are the hills, these are the woods,\r\nThese are my starry solitudes;\r\nAnd there the river by whose brink\r\nThe roaring lions come to drink."}),"\n",(0,t.jsx)(n.p,{children:"I see the others far away\r\nAs if in firelit camp they lay,\r\nAnd I, like to an Indian scout,\r\nAround their party prowled about."}),"\n",(0,t.jsx)(n.p,{children:"So when my nurse comes in for me,\r\nHome I return across the sea,\r\nAnd go to bed with backward looks\r\nAt my dear land of Story-books."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     VIII\r\n                              Armies in the Fire\n"})}),"\n",(0,t.jsx)(n.p,{children:"The lamps now glitter down the street;\r\nFaintly sound the falling feet;\r\nAnd the blue even slowly falls\r\nAbout the garden trees and walls."}),"\n",(0,t.jsx)(n.p,{children:"Now in the falling of the gloom\r\nThe red fire paints the empty room:\r\nAnd warmly on the roof it looks,\r\nAnd flickers on the back of books."}),"\n",(0,t.jsx)(n.p,{children:"Armies march by tower and spire\r\nOf cities blazing, in the fire;--\r\nTill as I gaze with staring eyes,\r\nThe armies fall, the lustre dies."}),"\n",(0,t.jsx)(n.p,{children:"Then once again the glow returns;\r\nAgain the phantom city burns;\r\nAnd down the red-hot valley, lo!\r\nThe phantom armies marching go!"}),"\n",(0,t.jsx)(n.p,{children:"Blinking embers, tell me true\r\nWhere are those armies marching to,\r\nAnd what the burning city is\r\nThat crumbles in your furnaces!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      IX\r\n                                The Little Land\n"})}),"\n",(0,t.jsx)(n.p,{children:"When at home alone I sit\r\nAnd am very tired of it,\r\nI have just to shut my eyes\r\nTo go sailing through the skies--\r\nTo go sailing far away\r\nTo the pleasant Land of Play;\r\nTo the fairy land afar\r\nWhere the Little People are;\r\nWhere the clover-tops are trees,\r\nAnd the rain-pools are the seas,\r\nAnd the leaves, like little ships,\r\nSail about on tiny trips;\r\nAnd above the Daisy tree\r\nThrough the grasses,\r\nHigh o'erhead the Bumble Bee\r\nHums and passes."}),"\n",(0,t.jsx)(n.p,{children:"In that forest to and fro\r\nI can wander, I can go;\r\nSee the spider and the fly,\r\nAnd the ants go marching by,\r\nCarrying parcels with their feet\r\nDown the green and grassy street.\r\nI can in the sorrel sit\r\nWhere the ladybird alit.\r\nI can climb the jointed grass\r\nAnd on high\r\nSee the greater swallows pass\r\nIn the sky,\r\nAnd the round sun rolling by\r\nHeeding no such things as I."}),"\n",(0,t.jsx)(n.p,{children:"Through that forest I can pass\r\nTill, as in a looking-glass,\r\nHumming fly and daisy tree\r\nAnd my tiny self I see,\r\nPainted very clear and neat\r\nOn the rain-pool at my feet.\r\nShould a leaflet come to land\r\nDrifting near to where I stand,\r\nStraight I'll board that tiny boat\r\nRound the rain-pool sea to float."}),"\n",(0,t.jsx)(n.p,{children:"Little thoughtful creatures sit\r\nOn the grassy coasts of it;\r\nLittle things with lovely eyes\r\nSee me sailing with surprise.\r\nSome are clad in armour green--\r\n(These have sure to battle been!)--\r\nSome are pied with ev'ry hue,\r\nBlack and crimson, gold and blue;\r\nSome have wings and swift are gone;--\r\nBut they all look kindly on."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:" When my eyes I once again\r\n Open, and see all things plain:\n"})}),"\n",(0,t.jsx)(n.p,{children:"High bare walls, great bare floor;\r\nGreat big knobs on drawer and door;\r\nGreat big people perched on chairs,\r\nStitching tucks and mending tears,\r\nEach a hill that I could climb,\r\nAnd talking nonsense all the time--\r\nO dear me,\r\nThat I could be\r\nA sailor on a the rain-pool sea,\r\nA climber in the clover tree,\r\nAnd just come back a sleepy-head,\r\nLate at night to go to bed."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                  Garden Days\r\n\r\n\r\n                                       I\r\n                                 Night and Day\n"})}),"\n",(0,t.jsx)(n.p,{children:"When the golden day is done,\r\nThrough the closing portal,\r\nChild and garden, Flower and sun,\r\nVanish all things mortal."}),"\n",(0,t.jsx)(n.p,{children:"As the blinding shadows fall\r\nAs the rays diminish,\r\nUnder evening's cloak they all\r\nRoll away and vanish."}),"\n",(0,t.jsx)(n.p,{children:"Garden darkened, daisy shut,\r\nChild in bed, they slumber--\r\nGlow-worm in the hallway rut,\r\nMice among the lumber."}),"\n",(0,t.jsx)(n.p,{children:"In the darkness houses shine,\r\nParents move the candles;\r\nTill on all the night divine\r\nTurns the bedroom handles."}),"\n",(0,t.jsx)(n.p,{children:"Till at last the day begins\r\nIn the east a-breaking,\r\nIn the hedges and the whins\r\nSleeping birds a-waking."}),"\n",(0,t.jsx)(n.p,{children:"In the darkness shapes of things,\r\nHouses, trees and hedges,\r\nClearer grow; and sparrow's wings\r\nBeat on window ledges."}),"\n",(0,t.jsx)(n.p,{children:"These shall wake the yawning maid;\r\nShe the door shall open--\r\nFinding dew on garden glade\r\nAnd the morning broken."}),"\n",(0,t.jsx)(n.p,{children:"There my garden grows again\r\nGreen and rosy painted,\r\nAs at eve behind the pane\r\nFrom my eyes it fainted."}),"\n",(0,t.jsx)(n.p,{children:"Just as it was shut away,\r\nToy-like, in the even,\r\nHere I see it glow with day\r\nUnder glowing heaven."}),"\n",(0,t.jsx)(n.p,{children:"Every path and every plot,\r\nEvery blush of roses,\r\nEvery blue forget-me-not\r\nWhere the dew reposes,"}),"\n",(0,t.jsx)(n.p,{children:'"Up!" they cry, "the day is come\r\nOn the smiling valleys:\r\nWe have beat the morning drum;\r\nPlaymate, join your allies!"'}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      II\r\n                                   Nest Eggs\n"})}),"\n",(0,t.jsx)(n.p,{children:"Birds all the summer day\r\nFlutter and quarrel\r\nHere in the arbour-like\r\nTent of the laurel."}),"\n",(0,t.jsx)(n.p,{children:"Here in the fork\r\nThe brown nest is seated;\r\nFor little blue eggs\r\nThe mother keeps heated."}),"\n",(0,t.jsx)(n.p,{children:"While we stand watching her\r\nStaring like gabies,\r\nSafe in each egg are the\r\nBird's little babies."}),"\n",(0,t.jsx)(n.p,{children:"Soon the frail eggs they shall\r\nChip, and upspringing\r\nMake all the April woods\r\nMerry with singing."}),"\n",(0,t.jsx)(n.p,{children:"Younger than we are,\r\nO children, and frailer,\r\nSoon in the blue air they'll be,\r\nSinger and sailor."}),"\n",(0,t.jsx)(n.p,{children:"We, so much older,\r\nTaller and stronger,\r\nWe shall look down on the\r\nBirdies no longer."}),"\n",(0,t.jsx)(n.p,{children:"They shall go flying\r\nWith musical speeches\r\nHigh overhead in the\r\nTops of the beeches."}),"\n",(0,t.jsx)(n.p,{children:"In spite of our wisdom\r\nAnd sensible talking,\r\nWe on our feet must go\r\nPlodding and walking."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      III\r\n                                  The Flowers\n"})}),"\n",(0,t.jsx)(n.p,{children:"All the names I know from nurse:\r\nGardener's garters, Shepherd's purse,\r\nBachelor's buttons, Lady's smock,\r\nAnd the Lady Hollyhock."}),"\n",(0,t.jsx)(n.p,{children:"Fairy places, fairy things,\r\nFairy woods where the wild bee wings,\r\nTiny trees for tiny dames--\r\nThese must all be fairy names!"}),"\n",(0,t.jsx)(n.p,{children:"Tiny woods below whose boughs\r\nShady fairies weave a house;\r\nTiny tree-tops, rose or thyme,\r\nWhere the braver fairies climb!"}),"\n",(0,t.jsx)(n.p,{children:"Fair are grown-up people's trees,\r\nBut the fairest woods are these;\r\nWhere, if I were not so tall,\r\nI should live for good and all."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      IV\r\n                                  Summer Sun\n"})}),"\n",(0,t.jsx)(n.p,{children:"Great is the sun, and wide he goes\r\nThrough empty heaven with repose;\r\nAnd in the blue and glowing days\r\nMore thick than rain he showers his rays."}),"\n",(0,t.jsx)(n.p,{children:"Though closer still the blinds we pull\r\nTo keep the shady parlour cool,\r\nYet he will find a chink or two\r\nTo slip his golden fingers through."}),"\n",(0,t.jsx)(n.p,{children:"The dusty attic spider-clad\r\nHe, through the keyhole, maketh glad;\r\nAnd through the broken edge of tiles\r\nInto the laddered hay-loft smiles."}),"\n",(0,t.jsx)(n.p,{children:"Meantime his golden face around\r\nHe bares to all the garden ground,\r\nAnd sheds a warm and glittering look\r\nAmong the ivy's inmost nook."}),"\n",(0,t.jsx)(n.p,{children:"Above the hills, along the blue,\r\nRound the bright air with footing true,\r\nTo please the child, to paint the rose,\r\nThe gardener of the World, he goes."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                       V\r\n                               The Dumb Soldier\n"})}),"\n",(0,t.jsx)(n.p,{children:"When the grass was closely mown,\r\nWalking on the lawn alone,\r\nIn the turf a hole I found,\r\nAnd hid a soldier underground."}),"\n",(0,t.jsx)(n.p,{children:"Spring and daisies came apace;\r\nGrasses hid my hiding place;\r\nGrasses run like a green sea\r\nO'er the lawn up to my knee."}),"\n",(0,t.jsx)(n.p,{children:"Under grass alone he lies,\r\nLooking up with leaden eyes,\r\nScarlet coat and pointed gun,\r\nTo the stars and to the sun."}),"\n",(0,t.jsx)(n.p,{children:"When the grass is ripe like grain,\r\nWhen the scythe is stoned again,\r\nWhen the lawn is shaven clear,\r\nThe my hole shall reappear."}),"\n",(0,t.jsx)(n.p,{children:"I shall find him, never fear,\r\nI shall find my grenadier;\r\nBut for all that's gone and come,\r\nI shall find my soldier dumb."}),"\n",(0,t.jsx)(n.p,{children:"He has lived, a little thing,\r\nIn the grassy woods of spring;\r\nDone, if he could tell me true,\r\nJust as I should like to do."}),"\n",(0,t.jsx)(n.p,{children:"He has seen the starry hours\r\nAnd the springing of the flowers;\r\nAnd the fairy things that pass\r\nIn the forests of the grass."}),"\n",(0,t.jsx)(n.p,{children:"In the silence he has heard\r\nTalking bee and ladybird,\r\nAnd the butterfly has flown\r\nO'er him as he lay alone."}),"\n",(0,t.jsx)(n.p,{children:"Not a word will he disclose,\r\nNot a word of all he knows.\r\nI must lay him on the shelf,\r\nAnd make up the tale myself."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      VI\r\n                                 Autumn Fires\n"})}),"\n",(0,t.jsx)(n.p,{children:"In the other gardens\r\nAnd all up the vale,\r\nFrom the autumn bonfires\r\nSee the smoke trail!"}),"\n",(0,t.jsx)(n.p,{children:"Pleasant summer over\r\nAnd all the summer flowers,\r\nThe red fire blazes,\r\nThe grey smoke towers."}),"\n",(0,t.jsx)(n.p,{children:"Sing a song of seasons!\r\nSomething bright in all!\r\nFlowers in the summer,\r\nFires in the fall!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      VII\r\n                                 The Gardener\n"})}),"\n",(0,t.jsx)(n.p,{children:"The gardener does not love to talk.\r\nHe makes me keep the gravel walk;\r\nAnd when he puts his tools away,\r\nHe locks the door and takes the key."}),"\n",(0,t.jsx)(n.p,{children:"Away behind the currant row,\r\nWhere no one else but cook may go,\r\nFar in the plots, I see him dig,\r\nOld and serious, brown and big."}),"\n",(0,t.jsx)(n.p,{children:"He digs the flowers, green, red, and blue,\r\nNor wishes to be spoken to.\r\nHe digs the flowers and cuts the hay,\r\nAnd never seems to want to play."}),"\n",(0,t.jsx)(n.p,{children:"Silly gardener! summer goes,\r\nAnd winter comes with pinching toes,\r\nWhen in the garden bare and brown\r\nYou must lay your barrow down."}),"\n",(0,t.jsx)(n.p,{children:"Well now, and while the summer stays,\r\nTo profit by these garden days\r\nO how much wiser you would be\r\nTo play at Indian wars with me!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                     VIII\r\n                            Historical Associations\n"})}),"\n",(0,t.jsx)(n.p,{children:"Dear Uncle Jim. this garden ground\r\nThat now you smoke your pipe around,\r\nhas seen immortal actions done\r\nAnd valiant battles lost and won."}),"\n",(0,t.jsx)(n.p,{children:"Here we had best on tip-toe tread,\r\nWhile I for safety march ahead,\r\nFor this is that enchanted ground\r\nWhere all who loiter slumber sound."}),"\n",(0,t.jsx)(n.p,{children:"Here is the sea, here is the sand,\r\nHere is the simple Shepherd's Land,\r\nHere are the fairy hollyhocks,\r\nAnd there are Ali Baba's rocks.\r\nBut yonder, see! apart and high,\r\nFrozen Siberia lies; where I,\r\nWith Robert Bruce William Tell,\r\nWas bound by an enchanter's spell."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                    ENVOYS\r\n\r\n\r\n                                       I\r\n                            To Willie and Henrietta\r\n\r\n If two may read aright\r\n These rhymes of old delight\r\n And house and garden play,\n"})}),"\n",(0,t.jsx)(n.p,{children:"You too, my cousins, and you only, may."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:" You in a garden green\r\n With me were king and queen,\r\n Were hunter, soldier, tar,\n"})}),"\n",(0,t.jsx)(n.p,{children:"And all the thousand things that children are."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:" Now in the elders' seat\r\n We rest with quiet feet,\r\n And from the window-bay\n"})}),"\n",(0,t.jsx)(n.p,{children:"We watch the children, our successors, play."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:' "Time was," the golden head\r\n Irrevocably said;\r\n But time which one can bind,\n'})}),"\n",(0,t.jsx)(n.p,{children:"While flowing fast away, leaves love behind."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      II\r\n                                 To My Mother\n"})}),"\n",(0,t.jsx)(n.p,{children:"You too, my mother, read my rhymes\r\nFor love of unforgotten times,\r\nAnd you may chance to hear once more\r\nThe little feet along the floor."}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      III\r\n                                   To Auntie\n"})}),"\n",(0,t.jsx)(n.p,{children:'"Chief of our aunts"--not only I,\r\nBut all your dozen of nurselings cry--\r\n"What did the other children do?\r\nAnd what were childhood, wanting you?"'}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      IV\r\n                                   To Minnie\n"})}),"\n",(0,t.jsx)(n.p,{children:'The red room with the giant bed\r\nWhere none but elders laid their head;\r\nThe little room where you and I\r\nDid for awhile together lie\r\nAnd, simple, suitor, I your hand\r\nIn decent marriage did demand;\r\nThe great day nursery, best of all,\r\nWith pictures pasted on the wall\r\nAnd leaves upon the blind--\r\nA pleasant room wherein to wake\r\nAnd hear the leafy garden shake\r\nAnd rustle in the wind--\r\nAnd pleasant there to lie in bed\r\nAnd see the pictures overhead--\r\nThe wars about Sebastopol,\r\nThe grinning guns along the wall,\r\nThe daring escalade,\r\nThe plunging ships, the bleating sheep,\r\nThe happy children ankle-deep\r\nAnd laughing as they wade:\r\nAll these are vanished clean away,\r\nAnd the old manse is changed to-day;\r\nIt wears an altered face\r\nAnd shields a stranger race.\r\nThe river, on from mill to mill,\r\nFlows past our childhood\'s garden still;\r\nBut ah! we children never more\r\nShall watch it from the water-door!\r\nBelow the yew--it still is there--\r\nOur phantom voices haunt the air\r\nAs we were still at play,\r\nAnd I can hear them call and say:\r\n"How far is it to Babylon?"'}),"\n",(0,t.jsx)(n.p,{children:'Ah, far enough, my dear,\r\nFar, far enough from here--\r\nSmiling and kind, you grace a shelf\r\nToo high for me to reach myself.\r\nReach down a hand, my dear, and take\r\nThese rhymes for old acquaintance\' sake!\r\nYet you have farther gone!\r\n"Can I get there by candlelight?"\r\nSo goes the old refrain.\r\nI do not know--perchance you might--\r\nBut only, children, hear it right,\r\nAh, never to return again!\r\nThe eternal dawn, beyond a doubt,\r\nShall break on hill and plain,\r\nAnd put all stars and candles out\r\nEre we be young again.'}),"\n",(0,t.jsx)(n.p,{children:"To you in distant India, these\r\nI send across the seas,\r\nNor count it far across.\r\nFor which of us forget\r\nThe Indian cabinets,\r\nThe bones of antelope, the wings of albatross,\r\nThe pied and painted birds and beans,\r\nThe junks and bangles, beads and screens,\r\nThe gods and sacred bells,\r\nAnd the load-humming, twisted shells!\r\nThe level of the parlour floor\r\nWas honest, homely, Scottish shore;\r\nBut when we climbed upon a chair,\r\nBehold the gorgeous East was there!\r\nBe this a fable; and behold\r\nMe in the parlour as of old,\r\nAnd Minnie just above me set\r\nIn the quaint Indian cabinet!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                       V\r\n                               To My Name-child\r\n\r\n                                       1\n"})}),"\n",(0,t.jsx)(n.p,{children:"Some day soon this rhyming volume, if you learn with proper speed,\r\nLittle Louis Sanchez, will be given you to read.\r\nThen you shall discover, that your name was printed down\r\nBy the English printers, long before, in London town."}),"\n",(0,t.jsx)(n.p,{children:"In the great and busy city where the East and West are met,\r\nAll the little letters did the English printer set;\r\nWhile you thought of nothing, and were still too young to play,\r\nForeign people thought of you in places far away."}),"\n",(0,t.jsx)(n.p,{children:"Ay, and when you slept, a baby, over all the English lands\r\nOther little children took the volume in their hands;\r\nOther children questioned, in their homes across the seas:\r\nWho was little Louis, won't you tell us, mother, please?"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                       2\n"})}),"\n",(0,t.jsx)(n.p,{children:"Now that you have spelt your lesson, lay it down and go and play,\r\nSeeking shells and seaweed on the sands of Monterey,\r\nWatching all the mighty whalebones, lying buried by the breeze,\r\nTiny sandpipers, and the huge Pacific seas."}),"\n",(0,t.jsx)(n.p,{children:"And remember in your playing, as the sea-fog rolls to you,\r\nLong ere you could read it, how I told you what to do;\r\nAnd that while you thought of no one, nearly half the world away\r\nSome one thought of Louis on the beach of Monterey!"}),"\n",(0,t.jsx)(n.pre,{children:(0,t.jsx)(n.code,{children:"                                      VI\r\n                                 To Any Reader\n"})}),"\n",(0,t.jsx)(n.p,{children:"As from the house your mother sees\r\nYou playing round the garden trees,\r\nSo you may see, if you will look\r\nThrough the windows of this book,\r\nAnother child, far, far away,\r\nAnd in another garden, play.\r\nBut do not think you can at all,\r\nBy knocking on the window, call\r\nThat child to hear you.  He intent\r\nIs all on his play-business bent.\r\nHe does not hear, he will not look,\r\nNor yet be lured out of this book.\r\nFor, long ago, the truth to say,\r\nHe has grown up and gone away,\r\nAnd it is but a child of air\r\nThat lingers in the garden there."}),"\n",(0,t.jsx)(n.p,{children:"End"})]})}function c(e={}){const{wrapper:n}={...(0,h.R)(),...e.components};return n?(0,t.jsx)(n,{...e,children:(0,t.jsx)(d,{...e})}):d(e)}},28453:(e,n,r)=>{r.d(n,{R:()=>s,x:()=>o});var a=r(96540);const t={},h=a.createContext(t);function s(e){const n=a.useContext(h);return a.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function o(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(t):e.components||t:s(e.components),a.createElement(h.Provider,{value:n},e.children)}}}]);